            vypocet += x;
        }
        return vypocet;
    }
    // paralelně zapojené rezistory
    private double Paralelne(int[] rezistory)
    {
        double vypocet = 0;

        for (int i = 0; i < rezistory.Length; i++)
        {
            vypocet += 1 / (float)rezistory[i];
        }
        vypocet = Math.Round(1 / vypocet, 2);

        return vypocet;
    }

